package com.example.myapplication.model;

import java.util.ArrayList;
import java.util.List;

public class MockBrandData {

    public static List<Brand> getMockBrands() {
        List<Brand> list = new ArrayList<>();

        list.add(createBrand(1, "Zara", "Thời trang", "https://statics.vincom.com.vn/http/vincom-ho/thuong_hieu/anh_logo/ZARA.png/ffeb6a1b7693ac1bfa9e375d554e00f7.webp",
                "Zara nổi tiếng với thời trang hiện đại và nhanh chóng.", 120, "FOLLOWED"));
        list.add(createBrand(2, "Prada", "Mỹ phẩm", "data:image/png;base64,",
                "Chanel mang đến sự sang trọng và tinh tế.", 95, "UNFOLLOWED"));
        list.add(createBrand(3, "Hermès", "Phụ kiện", "data:image/png;base64,",
                "Hermès nổi tiếng với túi xách và khăn choàng cao cấp.", 87, "FOLLOWED"));
        list.add(createBrand(4, "Chanel", "Mỹ phẩm", "data:image/png;base64,",
                "Chanel mang đến sự sang trọng và tinh tế.", 95, "UNFOLLOWED"));
        list.add(createBrand(5, "GUCCI", "Mỹ phẩm", "data:image/png;base64,",
                "Chanel mang đến sự sang trọng và tinh tế.", 95, "UNFOLLOWED"));

        return list;
    }

    private static Brand createBrand(int id, String name, String type, String avatar,
                                     String description, int productCount, String followStatus) {
        Brand brand = new Brand();
        brand.setBrandId(id);
        brand.setBrandName(name);
        brand.setBrandType(type);
        brand.setBrandAvatar(avatar);
        brand.setBrandDescription(description);
        brand.setNumberProduct(productCount);
        brand.setFollowStatus(followStatus);
        return brand;
    }
}
